sort_desc (
  sum by (label_beta_kubernetes_io_instance_type) (kube_node_labels{label_eks_amazonaws_com_capacity_type="SPOT"})
)

sort_desc (
sum_over_time(
  sum by (pod) (

      # See description for label_replace
      label_replace(
          # If requests is bigger than usage, use requests. Otherwise use usage.
          (
            (
                zone_capacity_instance_namespace_node_pod:pod_cpu_requests_instance_cpu_price:spot_pod_cpu_requests_cost{namespace="$namespace", pod=~"$pod"}
                > 
                zone_capacity_instance_namespace_node_pod:pod_cpu_usage_instance_cpu_price:spot_pod_cpu_usage_cost{namespace="$namespace", pod=~"$pod"}
            )
            OR
            zone_capacity_instance_namespace_node_pod:pod_cpu_usage_instance_cpu_price:spot_pod_cpu_usage_cost{namespace="$namespace", pod=~"$pod"}
          )
          OR
          # If requests is bigger than usage, use requests. Otherwise use usage.
          (
            (
                capacity_instance_namespace_node_pod:pod_cpu_requests_instance_cpu_price:on_demand_pod_cpu_requests_cost{namespace="$namespace", pod=~"$pod"}
                >
                capacity_instance_namespace_node_pod:pod_cpu_usage_instance_cpu_price:on_demand_pod_cpu_usage_cost{namespace="$namespace", pod=~"$pod"}
            )
            OR
            zone_capacity_instance_namespace_node_pod:pod_cpu_usage_instance_cpu_price:spot_pod_cpu_usage_cost{namespace="$namespace", pod=~"$pod"}
          )
      , "resource", "CPU", "","")

      OR 

      label_replace(
          # If requests is bigger than usage, use requests. Otherwise use usage.
          (
            (
                zone_capacity_instance_namespace_node_pod:pod_memory_requests_instance_mem_price:spot_pod_mem_requests_cost{namespace="$namespace", pod=~"$pod"}
                >
                zone_capacity_instance_namespace_node_pod:pod_memory_usage_instance_mem_price:spot_pod_mem_usage_cost{namespace="$namespace", pod=~"$pod"}
            )
            OR
            zone_capacity_instance_namespace_node_pod:pod_memory_usage_instance_mem_price:spot_pod_mem_usage_cost{namespace="$namespace", pod=~"$pod"}
          )
          OR
          # If requests is bigger than usage, use requests. Otherwise use usage.
          (
            (
                capacity_instance_namespace_node_pod:pod_memory_requests_instance_mem_price:on_demand_pod_mem_requests_cost{namespace="$namespace", pod=~"$pod"}
                >
                capacity_instance_namespace_node_pod:pod_memory_usage_instance_mem_price:on_demand_pod_mem_usage_cost{namespace="$namespace", pod=~"$pod"}
            ) 
            OR capacity_instance_namespace_node_pod:pod_memory_usage_instance_mem_price:on_demand_pod_mem_usage_cost{namespace="$namespace", pod=~"$pod"}
          )
      , "resource", "memory", "","")

)
[1d:1h])
)

